import React, { useState } from 'react'
import { IState as Props } from "../App";

interface IProps {
    setToys: React.Dispatch<React.SetStateAction<Props["toys"]>>
    toys: Props["toys"]
}

const AddToList: React.FC<IProps> = ({setToys, toys}) => {

    const [input, setInput] = useState({
        name: "",
        size: "",
        speed: "",
        distance: "",
        numberOfWheels : "",
        classType: "",
        url: "",
        note: ""
    })

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    /*

        "name": "Im a Car too!",
        "size": "XL",
        "speed": "4",
        "distance": "500",
        "wheels": "2",
        "type": "car"

     */
    const handleClick = () => {
        if(!input.classType || !input.name || !input.size) return
        setToys([
            ...toys,
            {
                name: input.name,
                type: input.classType,
                size: input.size,
                wheels: parseInt(input.numberOfWheels),
                url: input.url,
                note: input.note
            }
        ])
        fetch("localhost:8080/main/toys",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body: JSON.stringify({
                name: input.name,
                classType: input.classType,
                size: input.size,
                numberOfWheels: parseInt(input.numberOfWheels)
            })

        }).then(()=>{
            console.log("New toy added")
        });                                             //added without ; ??

        setInput({
            classType: "",
            name: "",
            size: "",
            numberOfWheels : "",
            url: "",
            note: ""
        })
    }

    return (
        <div className="AddToList">
            <input
                type="text"
                onChange={handleChange}
                className="AddToList-input"
                name="classType"
                value={input.classType}
                placeholder="Class type"
            />
            <input
                type="text"
                onChange={handleChange}
                className="AddToList-input"
                name="name"
                value={input.name}
                placeholder="Name"
            />
            <input
                type="text"
                onChange={handleChange}
                className="AddToList-input"
                name="size"
                value={input.size}
                placeholder="Size"
            />
            <input
                type="text"
                onChange={handleChange}
                className="AddToList-input"
                name="numberOfWheels"
                value={input.numberOfWheels}
                placeholder="Number of wheels"
            />
            <input
                type="text"
                onChange={handleChange}
                className="AddToList-input"
                name="url"
                value={input.url}
                placeholder="Image Url"
            />
            <input
                type="number"
                onChange={handleChange}
                className="AddToList-input"
                name="distance"
                value={input.distance}
                placeholder="Image Url"
            />
            <textarea
                onChange={handleChange}
                className="AddToList-input"
                name="note"
                value={input.note}
                placeholder="Note"
            />
            <button
                onClick={handleClick}
                className="AddToList-btn"
            >
                Add to List
            </button>
        </div>
    )
}

export default AddToList
